import UIKit

protocol ListPresentationLogic {
    func presentFetchedForecasts(response: List.Forecasts.Response)
}

class ListPresenter: ListPresentationLogic {
    weak var viewController: ListDisplayLogic?

    func presentFetchedForecasts(response: List.Forecasts.Response) {
        var displayedForecasts = [List.Forecasts.ViewModel.DisplayedForecast]()
        response.forecasts.keys.forEach { city in
            guard let forecast = response.forecasts[city] else { return }

            let displayedForecast = List.Forecasts.ViewModel.DisplayedForecast(city: city, forecast: forecast)
            displayedForecasts.append(displayedForecast)
        }

        let viewModel = List.Forecasts.ViewModel(displayedForecasts: displayedForecasts.sorted(by: { $0.city < $1.city }))
        viewController?.displayCurrentForecasts(viewModel: viewModel)
    }
}
